function LinearGradient(a, b, c, d) {
    this.px0 = isNaN(a) || 0 > a || 100 < a ? 0 : a;
    this.py0 = isNaN(b) || 0 > b || 100 < b ? 0 : b;
    this.px1 = isNaN(c) || 0 > c || 100 < c ? 100 : c;
    this.py1 = isNaN(d) || 0 > d || 100 < d ? 100 : d;
    this.colorsStop = [];
    this.addColorStop = function (a, b) {
        if (isNaN(a) || 0 > a || 100 < a)throw new TypeError("O indice de parada deve ser um numero entre 0 e 100");
        b instanceof Color || (b = Color.parse(b));
        this.colorsStop[this.colorsStop.length] = [a, b];
    }
};
