function RadialGradient(a, b, c, d, e) {
    this.r = isNaN(c) || 0 > c || 100 < c ? 50 : c;
    this.cx = isNaN(a) || 0 > a || 100 < a ? 50 : a;
    this.cy = isNaN(b) || 0 > b || 100 < b ? 50 : b;
    this.fx = isNaN(d) || 0 > d || 100 < d ? 50 : d;
    this.fy = isNaN(e) || 0 > e || 100 < e ? 50 : e;
    this.colorsStop = [];
    this.addColorStop = function (a, b) {
        if (isNaN(a) || 0 > a || 100 < a)throw new TypeError("O indice de parada deve ser um numero entre 0 e 100");
        b instanceof Color || (b = Color.parse(b));
        this.colorsStop.push([a, b])
    }
};
